package Day2.Javaprogram;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 10;

        if (n > 5) {
            System.out.println("The number is greater than 5.");
        } else {
            System.out.println("The number is 5 or less.");
        }
	}

}
